import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import qrcode from "./images/qr.jpg"
const Payment = () => {
    const [showCard, setShowCard] = useState(false);
    const [showQR, setShowQR] = useState(false);
    const [receipt, setReceipt] = useState(null);
    const navigate = useNavigate(); // Initialize useNavigate

    const handleCardPayment = () => {
        setShowCard(true);
        setShowQR(false);
        setReceipt(null);
    };

    const handleQRPayment = () => {
        setShowQR(true);
        setShowCard(false);
        setReceipt(null);
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        const formData = new FormData(event.target);
        const receiptData = {
            cardholderName: formData.get('cardholder-name'),
            cardNumber: formData.get('card-number'),
            expiryDate: formData.get('expiry-date'),
            cvv: formData.get('cvv'),
            amount: formData.get('amount'),
        };
        setReceipt(receiptData);
    };

    return (
        <div className="container">
            <style>
                {`
                body {
                    font-family: Arial, sans-serif;
                    background-color: #f2f2f2;
                    display: flex;
                    justify-content: center;
                    align-items: center;
                    height: 100vh;
                    margin: 0;
                }

                .container {
                    background-color: #fff;
                    padding: 20px;
                    border-radius: 10px;
                    box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
                    max-width: 400px;
                    width: 100%;
                    text-align: center;
                }

                h1 {
                    margin-bottom: 20px;
                    font-size: 24px;
                }

                .payment-method button {
                    margin: 5px;
                    padding: 10px;
                    border: none;
                    border-radius: 5px;
                    background-color: #4CAF50;
                    color: white;
                    cursor: pointer;
                }

                .payment-method button:hover {
                    background-color: #45a049;
                }

                .payment-form {
                    display: flex;
                    flex-direction: column;
                    align-items: center;
                }

                .payment-form label {
                    margin-bottom: 5px;
                    font-weight: bold;
                }

                .payment-form input {
                    margin-bottom: 15px;
                    padding: 10px;
                    border: 1px solid #ccc;
                    border-radius: 5px;
                    width: 100%;
                }

                button[type="submit"] {
                    padding: 10px;
                    background-color: #4CAF50;
                    color: white;
                    border: none;
                    border-radius: 5px;
                    cursor: pointer;
                }

                button[type="submit"]:hover {
                    background-color: #45a049;
                }

                img {
                    max-width: 100%;
                    height: auto;
                    display: block;
                    border: 2px solid #333; /* Adjust color and thickness */
                    padding: 4px; /* Space between image and border */
                    border-radius: 8px;
                    transition: transform 0.3s ease;
                }

                img:hover {
                    transform: scale(1.05);
                }

                .container {
                    position: relative;
                    height: 400px; /* Set your desired container height */
                }
                `}
            </style>
            <h1>Payment</h1>
            <div className="payment-method">
                <button onClick={handleCardPayment}>Card Payment</button>
                <button onClick={handleQRPayment}>QR Code Payment</button>
            </div>
            <button onClick={() => navigate(-1)} className="back-button">Back</button> {/* Added Back button */}
            {showCard && (
                <div className="payment-form">
                    <form id="card-form" onSubmit={handleSubmit}>
                        <label htmlFor="cardholder-name">Cardholder Name</label>
                        <input type="text" id="cardholder-name" name="cardholder-name" required />

                        <label htmlFor="card-number">Card Number</label>
                        <input type="text" id="card-number" name="card-number" required />

                        <label htmlFor="expiry-date">Expiry Date</label>
                        <input type="text" id="expiry-date" name="expiry-date" placeholder="MM/YY" required />

                        <label htmlFor="cvv">CVV</label>
                        <input type="text" id="cvv" name="cvv" required />
                        
                        <label htmlFor="Amount">Amount</label>
                        <input type="text" id="Amount" name="Amount" required />

                        <button type="submit">Pay Now</button>
                    </form>
                </div>
            )}
            {showQR && (
                <div className="payment-form">
                    <p>Scan the QR code to make a payment:</p>
                    <img src={qrcode} alt="QR Code" />
                </div>
            )}
            {receipt && (
                <div id="receipt">
                    <h2>Payment Receipt</h2>
                    <p><strong>Cardholder Name:</strong> {receipt.cardholderName}</p>
                    <p><strong>Card Number:</strong> {receipt.cardNumber}</p>
                    <p><strong>Expiry Date:</strong> {receipt.expiryDate}</p>
                    <p><strong>CVV:</strong> {receipt.cvv}</p>
                    
                    <button onClick={() => window.print()}>Print Receipt</button>
                </div>
            )}
        </div>
    );
};

export default Payment;
